mapper.insert:
INSERT INTO article
	SET regDate = NOW()
		, updateDate = NOW()
		, title = #{title}
		, body = #{body}
		, memberId = #{memberId}
		, boardId = #{boardId}

mapper.select:
SELECT A.*, M.nickName FROM article as A INNER JOIN Member as M ON A.memberId = M.Id WHERE A.boardId = #{boardId}
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.body LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( A.title LIKE CONCAT('%', #{searchKeyword}, '%') OR A.body LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY A.id DESC LIMIT #{pageStart}, #{pageArticles}

mapper.update:
UPDATE article

		updateDate = NOW()

			, title = #{title}


			, body = #{body}


	WHERE id = #{id}
